import { useState, useEffect } from "react";
import React from "react";
import { useParams } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { useNavigate, Link } from "react-router-dom";
import * as Yup from "yup";
import { toast } from "react-toastify";
import {
  useAddContactMutation,
  useContactQuery,
  useUpdateContactMutation,
} from "../services/contactsApi";
import "./AddEditContact.css";
import { Formik } from "formik";
import { TextField } from "./TextField";

const initialState = {
  name: "",
  dateOfBirth: "",
  email: "",
  contact: "",
};

const AddEditContact = () => {
  const [formValue, setFormValue] = useState(initialState);
  const [editMode, setEditMode] = useState(false);
  const [addContact] = useAddContactMutation();
  const [updateContact] = useUpdateContactMutation();
  const { name, dateOfBirth, email, contact } = formValue;
  const navigate = useNavigate();
  const { id } = useParams();

  const { data, error, isLoading, isFetching } = useContactQuery(id!);

  const validate = Yup.object({
    name: Yup.string()
      .max(15, "Must be 15 characters or less")
      .required("Required"),
    dateOfBirth: Yup.string()
      .max(20, "Must be 20 characters or less")
      .required("Required"),
    email: Yup.string()
      .email("Email is invalid")
      .required("Email is required"),
    contact: Yup.string().required("Password is required"),
  });

  useEffect(() => {
    if (id) {
      setEditMode(true);
      if (data) {
        setFormValue({ ...data });
      }
    } else {
      setEditMode(false);
      if (data) {
        setFormValue({ ...initialState });
      }
    }
  }, [id, data]);

  const handleSubmit = async (e: any) => {
    e.preventDefault();
    const emailregex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const contactregex = /^([+]\d{2}[ ])?\d{10}$/;
    if (!name && !email && !contact) {
      toast.error("Please provide value into each input field");
    } else if (name.length > 15) {
      toast.error("Name should be less than 15 Letters");
    } else if (!email) {
      toast.error("Email Cannot Be Empty!");
    } else if (!emailregex.test(email)) {
      toast.error("This is not a valid email format!");
    } else if (!contact) {
      toast.error("Contact Number Cannot Be Empty!");
    } else if (!contactregex.test(contact)) {
      toast.error("This is not a valid contact Number!");
    } else {
      if (!editMode) {
        await addContact(formValue);
        navigate("/");
        toast.success("Contact Added Successfully");
      } else {
        await updateContact(formValue);
        navigate("/");
        setEditMode(false);
        toast.success("Contact Updated Successfully");
      }
    }
  };

  const handleInputChange = (e: any) => {
    let { name, value } = e.target;
    setFormValue({ ...formValue, [name]: value });
  };
  return (
    <Formik
      initialValues={{
        name: "",
        dateOfBirth: "",
        email: "",
        contact: "",
      }}
      validationSchema={validate}
      onSubmit={(values) => {
        console.log(values);
      }}
    >
      {(formik) => (
        <div style={{ marginTop: "100px" }}>
          <form
            style={{
              margin: "auto",
              padding: "15px",
              maxWidth: "400px",
              alignContent: "center",
            }}
            onSubmit={handleSubmit}
          >
            <label className="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Name..."
              value={name || ""}
              onChange={handleInputChange}
            />
            <label className="dateOfBirth">Date Of Birth</label>
            <input
              type="date"
              id="dateOfBirth"
              name="dateOfBirth"
              placeholder="DOB..."
              value={dateOfBirth || ""}
              onChange={handleInputChange}
            />
            <label className="email">Email</label>
            <input
              type="text"
              id="email"
              name="email"
              placeholder="Email..."
              value={email || ""}
              onChange={handleInputChange}
            />
            <label className="contact">Contact</label>
            <input
              type="text"
              id="contact"
              name="contact"
              placeholder="Contact No....."
              value={contact || ""}
              onChange={handleInputChange}
            />

            <input type="submit" value={id ? "Update" : "Save"} />
          </form>
          <Link to="/">
            <button className="btn btn-edit">Go Back</button>
          </Link>
        </div>
      )}
    </Formik>
  );
};

export default AddEditContact;
