import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
import { MDBBtn, MDBBtnGroup, MDBContainer } from "mdb-react-ui-kit";
import { connect } from "react-redux";

const Navbar = () => {
  const [value, setValue] = useState("");
  const [data, setData] = useState({});
  //const handleReset = () => {};
  const handleSearch = async (e: any) => {
    e.preventDefault();
    return await axios
      .get(`http://localhost:8000/contacts?q=${value}`)
      .then((response) => {
        setData(response.data);
        setValue("");
      })
      .catch((err) => console.log(err));
  };
  return (
    <div className="header">
      <Link to="/">
        <div className="logo">CONTACT APP</div>
        <MDBContainer>
          <form
            style={{
              margin: "auto",
              padding: "15px",
              maxWidth: "400px",
              alignContent: "center",
            }}
            className="d-flex input-group w-auto"
            onSubmit={handleSearch}
          >
            <input
              type="text"
              className="form-control"
              placeholder="Search Contact"
              value={value}
              onChange={(e) => setValue(e.target.value)}
            ></input>
            <MDBBtn type="submit" className="btn btn-search" color="dark">
              Search
            </MDBBtn>
            <MDBBtn
              className="btn btn-reset"
              color="info"
              // onClick={() => handleReset()}
            >
              Reset
            </MDBBtn>
          </form>

          <Link to="/add">
            <button className="btn btn-add">Add Contact</button>
          </Link>
        </MDBContainer>
      </Link>
    </div>
  );
};

export default Navbar;
