import { useState, useEffect, Component } from "react";
import React from "react";
import { useRef } from "react";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { useParams } from "react-router-dom";
import { useNavigate, Link } from "react-router-dom";
import { toast } from "react-toastify";
import { Resolver, useForm } from "react-hook-form";
import {
  useAddContactMutation,
  useContactQuery,
  useUpdateContactMutation,
} from "../services/contactsApi";
import "./AddEditContact.css";

type FormValues = {
  name1: string;
  dateOfBirth1: string;
  email1: string;
  contact1: string;
  message: any;
};

const AddEditContact = () => {
  const initialState = {
    name: "",
    dateOfBirth: "",
    email: "",
    contact: "",
  };

  const [formValues, setFormValues] = useState(initialState);
  const [editMode, setEditMode] = useState(false);
  const [addContact] = useAddContactMutation();
  const [updateContact] = useUpdateContactMutation();
  const { name, dateOfBirth, email, contact } = formValues;
  const navigate = useNavigate();
  const { id } = useParams();
  const { data, error, isLoading, isFetching } = useContactQuery(id!);

  useEffect(() => {
    if (id) {
      setEditMode(true);
      if (data) {
        setFormValues({ ...data });
      }
    } else {
      setEditMode(false);
      if (data) {
        setFormValues({ ...initialState });
      }
    }
  }, [id, data]);

  const handleOnSubmit = async (e: any) => {
    e.preventDefault();
    const emailregex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const contactregex = /^([+]\d{2}[ ])?\d{10}$/;
    if (!name && !email && !contact) {
      toast.error("Please provide value into each input field");
    } else if (name.length > 15) {
      toast.error("Name should be less than 15 Letters");
    } else if (!email) {
      toast.error("Email Cannot Be Empty!");
    } else if (!emailregex.test(email)) {
      toast.error("This is not a valid email format!");
    } else if (!contact) {
      toast.error("Contact Number Cannot Be Empty!");
    } else if (!contactregex.test(contact)) {
      toast.error("This is not a valid contact Number!");
    } else {
      if (!editMode) {
        await addContact(formValues);
        navigate("/");
        toast.success("Contact Added Successfully");
      } else {
        await updateContact(formValues);
        navigate("/");
        setEditMode(false);
        toast.success("Contact Updated Successfully");
      }
    }
  };

  const handleInputChange = (e: any) => {
    e.preventDefault();
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const validationSchema = yup.object().shape({
    name1: yup.string().required("name is required"),
    dateOfBirth: yup.string().required("dateOfBirth is required"),
    email: yup.string().required("email is required"),
    contact: yup.string().required("contact is required"),
  });
  const resolver: Resolver<FormValues> = async (values) => {
    return {
      values: values.name1 ? values : {},
      errors: !values.name1
        ? {
            name1: {
              type: "required",
              message: "This is required.",
            },
          }
        : {},
    };
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<FormValues>({
    resolver,
  });

  return (
    <div style={{ marginTop: "100px" }}>
      <form
        style={{
          margin: "auto",
          padding: "15px",
          maxWidth: "400px",
          alignContent: "center",
        }}
        onSubmit={handleSubmit(handleOnSubmit)}
      >
        <div>
          <label className="name">Name</label>
          <input
            type="text"
            id="name"
            placeholder="Name..."
            value={name || ""}
            {...register("name1", { required: true, maxLength: 30 })}
            onChange={handleInputChange}
          />
        </div>
        {errors?.name1 && <p>{errors?.name1?.message}</p>}
        <div>
          <label className="dateOfBirth">Date Of Birth</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            placeholder="DOB..."
            value={dateOfBirth || ""}
            onChange={handleInputChange}
          />
        </div>
        <label className="email">Email</label>
        <input
          type="text"
          id="email"
          name="email"
          placeholder="Email..."
          value={email || ""}
          onChange={handleInputChange}
        />
        <label className="contact">Contact</label>
        <input
          type="text"
          id="contact"
          name="contact"
          placeholder="Contact No....."
          value={contact || ""}
          onChange={handleInputChange}
        />
        <input type="submit" value={id ? "Update" : "Save"} />
      </form>

      <Link to="/">
        <button className="btn btn-edit">Go Back</button>
      </Link>
    </div>
  );
};

export default AddEditContact;
