import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import { MDBBtn, MDBBtnGroup, MDBContainer } from "mdb-react-ui-kit";
import {
  useContactsQuery,
  useDeleteContactMutation,
} from "../services/contactsApi";
import "./Home.css";

const Home = () => {
  const { data, error, isLoading, isFetching, isSuccess } = useContactsQuery();
  const [value, setValue] = useState("");
  const [deleteContact] = useDeleteContactMutation();

  useEffect(() => {
    if (error) {
      toast.error("Something went Wrong");
    }
  }, [error]);

  const handleDelete = async (id: any) => {
    if (window.confirm("Are you sure that you wanted to delete that user ?")) {
      await deleteContact(id);
      toast.error("Contact Deleted Successfully");
    }
  };

  const handleReset = () => {};
  const handleSearch = async (e: any) => {
    e.preventefault();
    return await axios.get();
  };

  return (
    <div style={{ marginTop: "100px" }}>
      <h2>CONTACT APPLICATION </h2>
      <MDBContainer>
        <form
          style={{
            margin: "auto",
            padding: "15px",
            maxWidth: "400px",
            alignContent: "center",
          }}
          className="d-flex input-group w-auto"
          //onSubmit={handleSearch}
        >
          <input
            type="text"
            className="form-control"
            placeholder="Search Contact"
            value={value}
            onChange={(e) => setValue(e.target.value)}
          ></input>
          <MDBBtnGroup>
            <MDBBtn type="submit" className="btn btn-search" color="dark">
              Search
            </MDBBtn>
            <MDBBtn
              className="btn btn-reset"
              color="info"
              onClick={() => handleReset()}
            >
              Reset
            </MDBBtn>
          </MDBBtnGroup>
        </form>

        <Link to="/add">
          <button className="btn btn-add">Add Contact</button>
        </Link>
      </MDBContainer>
      <br />
      <br />
      <table className="styled-table">
        <thead>
          <tr>
            <th style={{ textAlign: "center" }}>ID</th>
            <th style={{ textAlign: "center" }}>Name</th>
            <th style={{ textAlign: "center" }}>Email</th>
            <th style={{ textAlign: "center" }}>Contact</th>
            <th style={{ textAlign: "center" }}>Action</th>
          </tr>
        </thead>
        <tbody>
          {data &&
            data.map((item: any, index: any) => {
              return (
                <tr key={item.id}>
                  <th scope="row">{index + 1}</th>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.contact}</td>
                  <td>
                    <Link to={`/view/${item.id}`}>
                      <button className="btn btn-view">View</button>
                    </Link>
                    <Link to={`/update/${item.id}`}>
                      <button className="btn btn-edit">Edit</button>
                    </Link>
                    <button
                      className="btn btn-delete"
                      onClick={() => handleDelete(item.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
