import { useState, useEffect, Fragment } from "react";
import React from "react";
import { useParams } from "react-router-dom";
import * as yup from "yup";
import { ErrorMessage, Field, Form, Formik } from "formik";
import "react-datepicker/dist/react-datepicker.css";
import { useNavigate, Link } from "react-router-dom";
import { toast } from "react-toastify";
import {
  useAddContactMutation,
  useContactQuery,
  useUpdateContactMutation,
} from "../services/contactsApi";
import "./AddEditContact.css";
import { isYieldExpression } from "typescript";

const AddEditContact = ({}) => {
  const initialState = {
    name: "",
    dateOfBirth: "",
    email: "",
    contact: "",
  };
  const formValidationSchema = yup.object().shape({
    name: yup.string().required("Name is required"),
    dateOfBirth: yup.string().required("Date of Birth is Required"),
    email: yup
      .string()
      .required("Email is required")
      .email("Please enter Valid email"),
  });
  const [formValues, setFormValues] = useState(initialState);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [addContact] = useAddContactMutation();
  const [updateContact] = useUpdateContactMutation();
  const { name, dateOfBirth, email, contact } = formValues;
  const navigate = useNavigate();
  const { id } = useParams();

  const { data, error, isLoading, isFetching } = useContactQuery(id!);

  useEffect(() => {
    if (id) {
      setEditMode(true);
      if (data) {
        setFormValues({ ...data });
      }
    } else {
      setEditMode(false);
      if (data) {
        setFormValues({ ...initialState });
      }
    }
  }, [id, data]);

  const handleSubmit = async (values: any) => {
    values.preventDefault();
    console.log("Submitted values", values);
    setIsSubmit(true);
    if (!editMode) {
      await addContact(formValues);
      navigate("/");
      toast.success("Contact Added Successfully");
    } else {
      await updateContact(formValues);
      navigate("/");
      setEditMode(false);
      toast.success("Contact Updated Successfully");
    }
  };

  const handleInputChange = (e: any) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  return (
    <Formik
      initialValues={initialState}
      validationSchema={formValidationSchema}
      onSubmit={(value) => handleSubmit(value)}
    >
      {({ values }) => (
        <Form
          style={{
            margin: "auto",
            padding: "15px",
            maxWidth: "400px",
            alignContent: "center",
          }}
        >
          <div>
            <label className="name">Name</label>
            <Field
              type="text"
              name="name"
              placeholder="Name..."
              value={formValues.name}
              onChange={handleInputChange}
            />
          </div>
          <p className="text-danger">
            <ErrorMessage name="name" />
          </p>
          <div>
            <label className="dateOfBirth">Date Of Birth</label>
            <Field
              type="date"
              id="dateOfBirth"
              name="dateOfBirth"
              placeholder="DOB..."
              value={dateOfBirth || ""}
              onChange={handleInputChange}
            />
          </div>
          <p className="text-danger">
            <ErrorMessage name="dateOfBirth" />
          </p>
          <div>
            <label className="email">Email</label>
            <Field
              type="text"
              id="email"
              name="email"
              placeholder="Email..."
              value={email || ""}
              onChange={handleInputChange}
            />
          </div>
          <p className="text-danger">
            <ErrorMessage name="email" />
          </p>

          <div>
            <label className="contact">Contact</label>
            <Field
              type="text"
              id="contact"
              name="contact"
              placeholder="Contact No....."
              value={contact || ""}
              onChange={handleInputChange}
            />
          </div>
          <input type="submit" value={id ? "Update" : "Save"} />

          <Link to="/">
            <button className="btn btn-edit">Go Back</button>
          </Link>
        </Form>
      )}
    </Formik>
  );
};

export default AddEditContact;
